import "./Contactus.css"
import React, { useState } from 'react';


const Contactus = () => {
  const [query, setQuery] = useState({Name:"", Email:"", ContactNo:"", Description:""});
  // const [title, setTitle] = useState("");
  // const [title, setTitle] = useState("");

  

  const handleChange = e => {
    const { name, value } = e.target;
    setQuery(prevState => ({
        ...prevState,
        [name]: value
    }));
};




return(
    <form className="contact" onSubmit={submitHandler}>
    <div className="form-group input ">
      <input type="Name" className="form-control" value={query.Name} onChange={handleChange} id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Your Name"/>
    </div>
    <div className="form-group input">
      <input type="Email" className="form-control" value={query.Email} onChange={handleChange} id="exampleInputPassword1" placeholder="Enter your Email"/>
    </div>
    <div className="form-group input">
      <input type="mobile" className="form-control" value={query.ContactNo} onChange={handleChange} id="exampleInputPassword1" placeholder="Enter your contact no."/>
    </div>
    <div className="form-group input">
    <textarea className="form-control" value={query.Description} onChange={handleChange} id="exampleFormControlTextarea1" rows="3" placeholder="Mention your query here"></textarea>
  </div>
    
   <div className="button">
    <button type="submit" className="btn btn-success ">Submit</button>
    </div>

  </form>
);

}
export default Contactus;
