import "./Contactus.css"
import React, { useState } from 'react';
import { HttpRequest } from "./FirebaseApi/HttpRequest";


const Contactus = () => {
  const [query, setQuery] = useState({ Name: "", Email: "", ContactNo: "", Description: "" });
  



  const handleChange = e => {
    const { name, value } = e.target;
    setQuery(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const submitHandler = (e) =>{
    e.preventDefault();
    console.log(query)
    const FIREBASE_DOMAIN = 'https://mytodolist-e06a2-default-rtdb.firebaseio.com/'


    fetch(`${FIREBASE_DOMAIN}/Contact-Us .json`, {
      method: "POST",
      body: JSON.stringify(query),
      headers: {
        "Content-Type": "application/json",
      },
    });
  

    // HttpRequest(query)
    setQuery({ Name: "", Email: "", ContactNo: "", Description: "" });
  }




  return (
    <form className="contact" onSubmit={submitHandler}>
      <div className="form-group input ">
        <input type="Name" className="form-control" value={query.Name} onChange={handleChange} name="Name" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter Your Name" />
      </div>
      <div className="form-group input">
        <input type="Email" className="form-control" value={query.Email} onChange={handleChange} name="Email" id="exampleInputPassword1" placeholder="Enter your Email" />
      </div>
      <div className="form-group input">
        <input type="Contact-no" className="form-control" value={query.ContactNo} onChange={handleChange} name="ContactNo" pattern="[7-9]{1}[0-9]{9}" id="exampleInputPassword1" placeholder="Enter your contact no." />
      </div>
      <div className="form-group input">
        <textarea className="form-control" value={query.Description} onChange={handleChange} name="Description" id="exampleFormControlTextarea1" rows="3" placeholder="Mention your query here"></textarea>
      </div>

      <div className="button">
        <button type="submit" className="btn btn-success ">Submit</button>
      </div>

    </form>
  );

}
export default Contactus;
