import React, { useState } from 'react';
import "./AddTodo.css";
export const AddTodo = (props) => {
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [date, setDate] = useState("");


    const submit = (e) => {
        e.preventDefault();
        if (!title || !desc) {
            alert("Title or Description or datecannot be blank");
        }
        else {
            props.addTodo(title, desc, date);
            setTitle("");
            setDesc("");
            setDate(" ")
        }
    }
    return (
        <div className="container my-3">
            <h3 style={{ textDecoration:"underline", color: "green", fontFamily:"italic"}}> <b> Add a Todo</b></h3>
            <form onSubmit={submit}>
                <div className="mb-3">
                    <label htmlFor="title" className="form-label">Todo Title</label>
                    <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} className="form-control field" id="title" aria-describedby="emailHelp" />

                </div>
                <div className="mb-3">
                    <label htmlFor="desc" className="form-label">Todo Description</label>
                    <textarea rows="3" value={desc} onChange={(e) => setDesc(e.target.value)} className="form-control textarea field" id="desc" />
                </div>
                <div className="mb-3">
                <label for="dueDate">Due Date (date and time):</label>
                <input type="date" value={time} onChange={(e) => setTime(e.target.value)} className="form-control textarea field" id="dueDate" name="dueDate"/>
                </div>

                <button type="submit" className="btn btn-sm btn-success">Add Todo</button>
            </form>
        </div>
    )
}
