import './App.css';
import Header from "./MyComponents/Header/Header";
import { Todos } from "./MyComponents/Todos";
import { Footer } from "./MyComponents/Footer";
import { AddTodo } from "./MyComponents/ToDos/AddTodo";
import { About } from "./MyComponents/AboutUs/About";
import React, { useState, useEffect } from 'react';
import { HttpRequest } from './MyComponents/FirebaseApi/HttpRequest';
import Contactus from "./MyComponents/Contactus"
// import { TodoItem } from './MyComponents/TodoItem';
import {
  BrowserRouter as Router,
  Switch,
  Route
} from "react-router-dom";


function App() {



  // const [style, setStyle] = useState("");
  // let itemStyle ;
  let initTodo;
  if (localStorage.getItem("todos") === null) {
    initTodo = [];
  }
  else {
    initTodo = JSON.parse(localStorage.getItem("todos"));
  }

  const [todos, setTodos] = useState(initTodo);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
    // let result = todos.map(({ date }) => date)
  }, [todos])


  const onDelete = (todo) => {
    setTodos(todos.filter((e) => {
      return e !== todo;
    }));
    localStorage.setItem("todos", JSON.stringify(todos));
  }

  const addTodo = (title, desc, date) => {
    let sno;
    if (todos.length === 0) {
      sno = 0;
    }
    else {
      sno = todos[todos.length - 1].sno + 1;
    }
    const myTodo = {
      sno: sno,
      title: title,
      desc: desc,
      date: date,
    }
    setTodos([...todos, myTodo]);

    HttpRequest(
      {
        sno: sno,
        title: title,
        desc: desc,
        // date: date,
      }
    );


  //   const today = new Date();

  //   var dd = today.getDate();
  //   var mm = today.getMonth() + 1;

  //   var yyyy = today.getFullYear();
  //   if (dd < 10) {
  //       dd = '0' + dd;
  //   }
  //   if (mm < 10) {
  //       mm = '0' + mm;
  //   }
  //    const currentDate = yyyy + '-' + mm + '-' + dd;

  //   console.log(currentDate)
  //   console.log(date)


  //   date===currentDate? setStyle("dateMatched"): setStyle("dateNotMatched")
  // }

  // const itemStyle={style}
  // style={itemStyle}
    }


  return ( 
    <> 
    <Router>
      <Header title="My Todos List"  /> 

      <Switch>
          <Route exact path="/" render={()=>{
            return(
            <>
            <AddTodo addTodo={addTodo} />
            <Todos todos={todos} onDelete={onDelete}  /> 
            </>)
          }}> 
          </Route>
          <Route exact path="/about">
            <About />
          </Route> 
          <Route exact path="/contact-us">
            <Contactus />
          </Route> 
        </Switch> 

      <Footer />
    </Router>
    </>
  );
}

export default App;
